mapper.select:
select	podt_code , podt_unit , podt_flag , man_flag , podt_qnt , podt_price , FN_GET_CODE_NAME(podt_code) AS codeName
from	Product 
where	podt_code = #{podtCode}

SELECT	res_code , res_usage , res_etc , FN_GET_CODE_NAME(res_code) AS codeName
FROM	Prdt_bom_res
WHERE	podt_code = #{podtCode}

SELECT	field , proc_code  , podt_code , proc_index , FN_GET_CODE_NAME(proc_code) AS codeName
FROM 	process_flowInfo 
WHERE	podt_code = #{podtCode}
