mapper.insert:
insert into process (
						proc_code,
						proc_flag,
						proc_emp_id	
					)
			values (
						#{procCode},
						#{procFlag},
						#{procEmpId}
					)

mapper.update:
update process
set
proc_flag = #{procFlag},
proc_emp_id	= #{procEmpId}
where proc_code = #{procCode}

update common_code
set
code_name = #{codeName}
where code = #{procCode}
