mapper.select:
select fac_no 
from facility_process
where proc_code = #{procCode}

mapper.insert:
insert into process (
						proc_code,
						proc_flag,
						proc_emp_id	
					)
			values (
						#{procCode},
						#{procFlag},
						#{procEmpId}
					)

insert into facility_process (
						fac_no,
						proc_code
					)
			values (
						#{facNo},
						#{procCode}
					)

mapper.update:
update process
set
proc_flag = #{procFlag},
proc_emp_id	= #{procEmpId}
where proc_code = #{procCode}

update common_code
set
code_name = #{codeName}
where code = #{procCode}
