mapper.select:
select COM_CODE, MAN_STARTDATE, COM_NAME, PODT_CODE, prod_detail(podt_code) podtName, MAN_GOALPERDAY, ORD_QNT, MAN_ETC from man_command_view where 1=1
and podt_code = #{podtCode}
and man_startdate = #{manStartDate}
order by com_code

mapper.update:
update manufacture_plan_d 
set plan_etc = '생산지시중' 
where plan_no_detail = #{planNoDetail}
