mapper.select:
select COM_CODE, COM_NAME, to_char(MAN_STARTDATE, 'yyyy-MM-dd') as manStartDate, PODT_CODE, prod_detail(podt_code) podtName, MAN_GOALPERDAY, ORD_QNT, COM_ETC from man_command_view where 1=1
and podt_code = #{podtCode}
and to_char(man_startdate, 'rrrr-MM-dd') >= #{startDate}
and #{endDate} >= to_char(man_startdate, 'rrrr-MM-dd')
order by com_code

mapper.update:
update manufacture_plan_d 
set plan_etc = '생산지시중' 
where plan_no_detail = #{planNoDetail}
