mapper.select:
select c.proc_code, prod_detail(c.proc_Code) as procName, to_char(c.man_startTime,'yyyy-MM-dd') as manStartdate, c.man_qnt, c.def_qnt, d.emp_id, d.emp_name, e.podt_code, prod_detail(e.podt_code)as podtName, e.man_goalperday from manufacture_process c join (select a.proc_code, prod_detail(a.proc_code), b.emp_id, b.emp_name from process a join employee_info b on a.proc_emp_id = b.emp_id where a.proc_code between 'PROC001' and 'PROC011' order by a.proc_code) d on c.proc_code = d.proc_code
join manufacture_command_d e on c.com_code = e.com_code
where c.com_Code= #{comCode}
