mapper.select:
select rsc_code ,  FN_GET_CODE_NAME(rsc_code) AS rscName , rsc_unit , rsc_prc ,rsc_man_code , suplcom_code , FN_GET_CODE_NAME(suplcom_code) AS suplcomName
from resources
where rsc_code = #{rscCode}

select * from ( select to_char(store_date , 'MM') AS month ,nvl(lag(sum(case when store_flag='입고' then ist_cnt else 0 end) - nvl((select SUM(rtngd_cnt) from resource_rtngd where rsc_code= #{rscCode} and to_char(rtngd_date,'MM') = to_char(store_date , 'MM') ),0) - sum(case when store_flag='출고' then ost_cnt else 0 end) + sum(case when store_flag='정산입고' then ist_cnt else 0 end) - sum(case when store_flag='정산출고' then ost_cnt else 0 end)) over (order by to_char(store_date , 'MM')),0) AS carryOver ,sum(case when store_flag='입고' then ist_cnt else 0 end) AS matInto ,sum(case when store_flag='출고' then ost_cnt else 0 end) AS matOut ,sum(case when store_flag='정산입고' then ist_cnt else 0 end) AS checkInto ,sum(case when store_flag='정산출고' then ost_cnt else 0 end) AS checkOut ,nvl((select SUM(rtngd_cnt) from resource_rtngd where rsc_code= #{rscCode} and to_char(rtngd_date,'MM') = to_char(store_date , 'MM') ),0) AS matReturn ,nvl(lag(sum(case when store_flag='입고' then ist_cnt else 0 end) - nvl((select SUM(rtngd_cnt) from resource_rtngd where rsc_code= #{rscCode} and to_char(rtngd_date,'MM') = to_char(store_date , 'MM') ),0) - sum(case when store_flag='출고' then ost_cnt else 0 end) + sum(case when store_flag='정산입고' then ist_cnt else 0 end) - sum(case when store_flag='정산출고' then ost_cnt else 0 end)) over (order by to_char(store_date , 'MM')),0)+sum(case when store_flag='입고' then ist_cnt else 0 end) - nvl((select SUM(rtngd_cnt) from resource_rtngd where rsc_code= #{rscCode} and to_char(rtngd_date,'MM') = to_char(store_date , 'MM') ),0) - sum(case when store_flag='출고' then ost_cnt else 0 end) + sum(case when store_flag='정산입고' then ist_cnt else 0 end) - sum(case when store_flag='정산출고' then ost_cnt else 0 end) AS matSum from resource_store r where rsc_code = #{rscCode} group by to_char(store_date , 'MM') union SELECT lpad(level-1,2,'0') ,0 ,0 ,0 ,0 ,0 ,0 ,0 from dual d where level not in
${vo.pmonth}
CONNECT BY LEVEL <=13
) order by month

mapper.update:
update resources
set
rsc_man_code = #{rscManCode},
suplcom_code = #{suplcomCode},
rsc_prc		 = #{rscPrc}
where rsc_code = #{rscCode}
