mapper.select:
SELECT O.ORDE_DATE, O.ORDR_NO, O.RSC_CODE, O.RSC_CNT, O.IST_REQ_DATE, FN_GET_CODE_NAME(O.RSC_CODE) AS RSC_NAME, R.RSC_PRC, R.RSC_UNIT, FN_GET_CODE_NAME(R.SUPLCOM_CODE) AS SUC_NAME, C.RSC_DEF_CNT, C.RSC_PASS_CNT FROM RESOURCE_ORDER O LEFT JOIN RESOURCES R ON O.RSC_CODE = R.RSC_CODE LEFT JOIN RESOURCE_IST_CHECK C ON O.ORDR_NO = C.ORDR_NO
and O.RSC_CODE = #{rscCode}
and R.SUPLCOM_CODE = #{sucCode}
and to_char(O.ORDE_DATE,'yyyyMMdd') >= to_char(#{ordeDate},'yyyyMMdd') AND to_char(O.ORDE_DATE,'yyyyMMdd') <= to_char(#{ordeDate2}, 'yyyyMMdd')
and O.ORDR_NO = #{ordrNo}
ORDER BY O.ORDR_NO
