mapper.update:
		UPDATE RESOURCE_ORDER 
        SET RSC_ST = '입고'
      	WHERE ORDR_NO = #{ordrNo}

mapper.select:
SELECT S.STORE_NO, S.RSC_CODE, FN_GET_CODE_NAME(S.RSC_CODE) AS RSC_NAME, FN_GET_RSC_UNIT(S.RSC_CODE) AS RSC_UNIT, FN_GET_RSC_PRC(S.RSC_CODE) AS RSC_PRC, S.IST_CNT, S.RSC_LOT, S.STORE_FLAG, S.STORE_DATE, FN_GET_CODE_NAME(R.SUPLCOM_CODE) AS SUC_NAME FROM RESOURCE_STORE S LEFT JOIN RESOURCES R ON S.RSC_CODE = R.RSC_CODE WHERE STORE_FLAG = '입고'
AND O.RSC_CODE = #{rscCode}
AND R.SUPLCOM_CODE = #{sucCode}
AND to_char(S.STORE_DATE,'yyyyMMdd') >= to_char(#{storeDate},'yyyyMMdd') AND to_char(S.STORE_DATE,'yyyyMMdd') <= to_char(#{storeDate2}, 'yyyyMMdd')
ORDER BY S.STORE_DATE

SELECT S.STORE_NO, S.RSC_CODE, FN_GET_CODE_NAME(S.RSC_CODE) AS RSC_NAME, FN_GET_RSC_UNIT(S.RSC_CODE) AS RSC_UNIT, FN_GET_RSC_PRC(S.RSC_CODE) AS RSC_PRC, S.OST_CNT, S.RSC_LOT, S.STORE_FLAG, S.STORE_DATE, FN_GET_CODE_NAME(R.SUPLCOM_CODE) AS SUC_NAME, STORE_ETC FROM RESOURCE_STORE S LEFT JOIN RESOURCES R ON S.RSC_CODE = R.RSC_CODE WHERE STORE_FLAG = '출고'
AND S.RSC_CODE = #{rscCode}
AND R.SUPLCOM_CODE = #{sucCode}
AND to_char(S.STORE_DATE,'yyyyMMdd') >= to_char(#{storeDate},'yyyyMMdd') AND to_char(S.STORE_DATE,'yyyyMMdd') <= to_char(#{storeDate2}, 'yyyyMMdd')
ORDER BY S.STORE_DATE

SELECT RSC_LOT , SUM(IST_CNT) AS IST_CNT, SUM(OST_CNT) AS OST_CNT, (SUM(IST_CNT)-SUM(OST_CNT)) AS RSC_CNT, RSC_CODE, FN_GET_CODE_NAME(RSC_CODE) AS RSC_NAME, FN_GET_RSC_UNIT(RSC_CODE) AS RSC_UNIT FROM RESOURCE_STORE
WHERE RSC_CODE = #{rscCode}
GROUP BY RSC_LOT , RSC_CODE ORDER BY RSC_CODE
